$(document).ready(function(){

	/*** SCROLL CAMPAÃ‘A ***/
	$('html, body').animate({scrollTop:$('#wrapper-content').position().top}, 250);
	
	init_home_resize();

	$('.nav-menu-slice').height($(window).height());
	

	/*** HAMBURGUESA MENU ***/
	$( "#nav-hamburguer, #nav-hamburguer-fixed" ).click(function() {


		$(this).toggleClass("is-active");

		/*var hHeader = $('header').height();
		$( "#wrapper-content .nav-menu-slice" ).css({
			top: hHeader
		});*/

		if($(this).hasClass('is-active')){
			//document.getElementById("page-in").addEventListener("click", myFunction);
			$( "#page-in" ).addClass('menu-open');
			$( ".nav-menu-slice" ).addClass('nav-open');
		}else{
			$( "#page-in" ).removeClass('menu-open');
			$( ".nav-menu-slice" ).removeClass('nav-open');
		}
		
	});

	$( ".overlay-menu" ).click(function() {
		if($('#page-in.menu-open')){
			console.log('entro')	
			$( "#page-in" ).removeClass('menu-open');
			$( ".nav-menu-slice" ).removeClass('nav-open');
			$( "#nav-hamburguer, #nav-hamburguer-fixed" ).removeClass('is-active');
		}
		
		
	})

	/*** OPEN DETAIL ***/
	$(".product .open-modal" ).click(function(e) {
		e.preventDefault();
		$('body, html').css('overflow', 'hidden');
		$('#detail-product').fadeIn();
		$('.thumbs').slick({
            slidesToShow: 5,
            slidesToScroll: 1,
            arrows: false,
            focusOnSelect: true,
            vertical: true,
            centerMode: true,
            asNavFor: '.slider'
        });
        $('.slider').slick({
            slidesToShow: 1,
            slidesToScroll: 1,
            asNavFor: '.thumbs',
            dots: false,
            fade: true,
            centerMode: true,
            prevArrow: '<button type="button" data-role="none" class="slick-prev" tabindex="0" role="button"><i class="fa fa-angle-left" aria-hidden="true"></i></button>',
            nextArrow: '<button type="button" data-role="none" class="slick-next" tabindex="0" role="button"><i class="fa fa-angle-right" aria-hidden="true"></i></button>'

            
        });
	});	


	/*** CLOSE DETAIL ***/
	$( "#close-modal" ).click(function(e) {
		e.preventDefault();
		$('body, html').css('overflow', 'auto');
		$('.thumbs').slick('unslick');
		$('.slider').slick('unslick');
		$('#detail-product').fadeOut(200);
	});	



	/*$('#products-affix').affix({
        offset: {     
            top: 400,
            bottom: 450
        }
    });*/

});

